import { drawer } from '../subassembly/drawer.js';
import { $el } from "../../../scripts/ui.js";
import { tooltip } from  '../subassembly/tooltip.js'
import { getUserInfo, putShareId } from '../apis.js'
import { apiKey } from "./apiKey.js";
import { toast } from '../subassembly/toast.js';
import { subscribe } from "../subassembly/subscribers.js";
import { dialog } from '../subassembly/dialog.js';

const getSelector = selector => {
    const element = document.querySelector(selector);
    element.css = function (style) {
        Object.assign(this.style, style);
        return this;
    };
    element.text = function (text) {
        this.innerText = text;
        return this;
    };
    element.val = function (val) {
        this.value = val;
        return this;
    };
    return element;
};
const levelMap = {
    1: 'Trial',
    2: 'Pro',
    3: 'Enterprise'
}
export async function profileDialog() {
    const res = await getUserInfo()
    const info = res.data
    sessionStorage.setItem('userInfo', JSON.stringify(info))
    const editShareId = () => {
        getSelector('#bizyair-profile-share-id').css({ display: 'none' })
        getSelector('#bizyair-profile-share-id-edit').css({ display: 'block' })
        getSelector('.bizyair-tooltip-edit').css({ display: 'none' })
        getSelector('.bizyair-tooltip-save').css({ display: 'block' })
    }
    const saveShareId = () => {
        dialog({
            title: 'Are you sure you want to modify it?',
            content: 'If you make this change, it will render any models you have previously shared unavailable, and you are only allowed to make this modification once per year.',
            noText: 'Cancel',
            yesText: 'Confirm',
            onYes: async () => {
                await putShareId({ share_id: getSelector('#bizyair-profile-share-id-edit').value })
                getSelector('#bizyair-profile-share-id').css({ display: 'block' })
                getSelector('#bizyair-profile-share-id-edit').css({ display: 'none' })
                getSelector('.bizyair-tooltip-save').css({ display: 'none' })
                getSelector('.bizyair-tooltip-edit').css({ display: 'block' })
                getSelector('#bizyair-profile-share-id').text(getSelector('#bizyair-profile-share-id-edit').value)
            }
        })
    }
    const copyText = text => {
        console.log(text)
        if (navigator.clipboard) {
            navigator.clipboard.writeText(text).then(() => {
                toast('Text has been copied to the clipboard.');
            }).catch(err => {
                toast.error('Unable to copy text: ', err);
            });
        } else {
            toast.error('The browser does not support the Clipboard API.');
          }
    }
    const generateShareIDMessage = (date) => {
        const today = new Date(date);

        const nextYear = new Date(today);
        nextYear.setFullYear(today.getFullYear() + 1);
        const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
        const formattedDate = nextYear.toLocaleDateString('en-US', options);
        return `You can only change your share ID once a year. You need to wait until 【${formattedDate}】 to make the modification.`;
    }
    const content =
        $el('div', {}, [

            $el('div.bizyair-profile-primary', {}, [
                $el('div', {}, [
                    $el('img', { src: 'data:image/png;base64,' }),
                ]),
                (info.name ? $el('div', {}, [`${info.name}`]) : '')
            ]),

            $el('div.bizyair-profile-primary', {}, [
                $el('div.bizyair-profile-primary-box', {}, [
                    'API Key:',
                    $el('span.bizyair-profile-password.margin-left-10', {
                        id: 'bizyair-profile-password'
                    }, [`${info.api_key}`]),
                    $el('div.bizyair-profile-operate', {}, [
                        tooltip({
                            tips: 'Edit',
                            content: $el('span.bizyair-icon-operate.bizyair-icon-edit', {
                                onclick: apiKey
                            })
                        })
                    ]),
                ])
            ]),
            // $el('div.bizyair-profile-primary', {}, [
            //     'Status: ',
            //     $el('span.margin-left-10', {}, [`${info.status}`]),
            // ]),
            $el('div.bizyair-profile-primary', {}, [
                'Level: ',
                $el('span.margin-left-10', {}, [`${levelMap[info.level]}`]),
            ]),
            $el('div.bizyair-profile-primary-box', {}, [
                $el('div.bizyair-profile-primary', {
                    style: {
                        display: 'flex',
                        alignItems: 'center'
                    }
                }, [
                    'Share ID: ',
                    $el('span.margin-left-10', {
                        style: {
                            display: 'block'
                        },
                        id: 'bizyair-profile-share-id'
                    }, [`${info.share_id}`]),
                    $el('input.margin-left-10', {
                        style: {
                            display: 'none'
                        },
                        id: 'bizyair-profile-share-id-edit',
                        value: info.share_id,
                        onkeyup: (e) => {
                            if (e.key === 'Enter') {
                                saveShareId()
                            }
                        }
                    }),
                ]),
                $el('div.bizyair-profile-operate', {}, [
                    (Date.now() - new Date(info.last_share_id_update_at) > 1000 * 60 * 60 * 24 * 365 ? tooltip({
                        class: 'bizyair-tooltip-edit',
                        style: {
                            display: 'block'
                        },
                        tips: 'Edit',
                        content: $el('span.bizyair-icon-operate.bizyair-icon-edit', {
                            onclick: editShareId
                        })
                    }): tooltip({
                        class: 'bizyair-tooltip-edit-none',
                        style: {
                            display: 'block'
                        },
                        placement: 'left',
                        tips: generateShareIDMessage(info.last_share_id_update_at),
                        content: $el('span.bizyair-icon-operate.bizyair-icon-edit.bizyair-icon-disabled', {})
                    })),
                    tooltip({
                        class: 'bizyair-tooltip-save',
                        style: {
                            display: 'none'
                        },
                        tips: 'Save',
                        content: $el('span.bizyair-icon-operate.bizyair-icon-save', {
                            onclick: saveShareId
                        })
                    }),
                    tooltip({
                        tips: 'Copy',
                        content: $el('span.bizyair-icon-operate.bizyair-icon-copy', {
                            onclick: () => copyText(info.share_id)
                        })
                    })
                ]),
            ]),
        ])
    drawer({
        title: ' ',
        content: content,
        direction: 'right',
        width: '600px'
    });
    subscribe('loginRefresh', e => {
        getSelector('#bizyair-profile-password').text(e)
    })
}
